INSERT INTO animal (id, nombre, apodo, aspectos_veterinarios, fecha_nacimiento, genero, foto, tipo_animal_id, historia, raza) VALUES (1, 'Kenai', 'Kenai el perro feliz', 'KENAI está completamente sano. Se entrega con chip, pasaporte, vacunas al día, test de enfermedades negativo de anaplasma, erliquia, filaria y leishmaniosis. Además, se entrega esterilizado.', '2022-01-01', 'Macho','https://img.huffingtonpost.es/files/image_720_480/uploads/2023/06/22/un-perro-de-raza-labrador.jpeg' ,1, 'KENAI fue recogido de la calle abandonado y desatendido, con una soga al cuello. ¿Quizás le tenían atado a la intemperie hasta que se hartaron de él?. No conocemos su pasado pero si sabemos que ahora, pasa a formar parte de la familia del refugio donde esperará una familia que se enamore de él y le cuide como a un miembro más.',  'Mestizo ' );
INSERT INTO animal (id, nombre, apodo, aspectos_veterinarios, fecha_nacimiento, genero, foto, tipo_animal_id, historia, raza) VALUES (2, 'Lola', 'LOLA necesita una oportunidad', 'LOLA es positiva a Leishmania, ya se encuentra con tratamiento.Se entrega con chip, pasaporte, vacunas al día y test de enfermedades negativo de anaplasma, erliquia, filaria. Además, se entrega esterilizada.', '2022-09-09', 'Hembra','https://elrefugioescuela.com/sofia/wp-content/uploads/2024/04/IMG_20240426_133133-1-2048x1152.jpg' ,1, 'Una compañera nos comentó que pertenecían hombre que ya no las quería y que prefirió confinarlas en una pequeña jaula de 2×2 metros en un polígono, sin tratamiento y casi sin comida ni agua en vez de buscarles un nuevo hogar. Hoy, han sido por fin, cedidas a nuestro refugio y las vamos a cuidar como se merecen y encontrarles un nuevo hogar donde sean un miembro más de la familia.', 'Mestizo' );
INSERT INTO animal (id, nombre, apodo, aspectos_veterinarios, fecha_nacimiento, genero, foto, tipo_animal_id, historia, raza) VALUES (3, 'Florita', 'La gatita FLORITA', 'FLORITA es positiva en CALICIVIRUS, algo que no le impide hacer vida normal. Eso sí, debe ser gatihija única o vivir con otros gatos también positivos en esta enfermdad. Con perros y humanos no es contagioso, por lo que no hay ningún problema. Por lo demás, es negativa a leucemia e inmuno, tiene todas sus vacunas en regla, chip, cartilla veterianaria, desparasitaciones y está esterilizada.', '2022-09-25', 'Hembra','https://elrefugioescuela.com/sofia/wp-content/uploads/2022/04/portada-adoptar-gata-rubia-y-blanca-linda-1024x619.png' ,2, 'FLORITA vivía en la calle y pertenecía a una colonia controlada cerca del refugio. Pero ella quería tener una familia que la protegiera y cuidara. Poco a poco empezó a mostrarse más sociable y confiada con los voluntarios a diferencia de sus compañeros de la colonia, que evitan acercarse más de lo necesario. Cada vez que veníamos, ella ya estaba allí y saludaba, nos hacía carantoñas y se dejaba coger, mimar y acariciar. No tenía miedo a los perros, a los que se acercaba sin dudar, con lo cual tenerla por allí empezaba a representar un peligro para todos e iba a suponer un disgusto. En pocos días, FLORITA empezó a formar parte oficialmente de nuestro refugio en una de nuestras gateras, teniendo así la oportunidad de encontrar un hogar. Mientras vivirá segura y recibiendo todas las atenciones.',  'Angora turco' );
INSERT INTO animal (id, nombre, apodo, aspectos_veterinarios, fecha_nacimiento, genero, foto, tipo_animal_id, historia, raza) VALUES (4, 'Vito', 'Solo amor para el perro VITO', 'VITO, a pesar de su joven edad, ha vivido historias duras, lo que han hecho que valore mucho las muestras de afecto. Es un perrito ágil y fuerte y lo demuestra en su forma de jugar. Es feliz con espacio donde poder correr y busca tener la atención de su familia. Gracias a los cuidados y el adiestramiento que ha recibido, ha cambiado muchísimo; ahora es mucho más obediente y socializa con otros perros muy pronto. En los paseos es más tranquilo y hace más caso a la persona que le pasea. Ya está aprendiendo a bañarse. Avanza a pasos agigantados. Su inteligencia y su nobleza hacen que aprenda y mejore muchísimo.', '2019-09-24', 'Macho','https://elrefugioescuela.com/sofia/wp-content/uploads/2022/03/vito-portada-neuva-1024x619.png' ,1, 'A VITO le cambió radicalmente su vida desde el momento en el que una buena amiga conoció su historia y trajo hasta nosotros. Supo que nuestro peludo sufrió maltrato físico en la casa donde vivía y había acabado en el Zoosanitario.',  'Cruce' );
INSERT INTO animal (id, nombre, apodo, aspectos_veterinarios, fecha_nacimiento, genero, foto, tipo_animal_id, historia, raza) VALUES (5, 'Billy', 'El simpático BILLY', 'BILLY es puro amor y dulzura pero, como todo gatito joven, está en edad de aprendizaje y curiosidad por todo, así que, los juegos gatunos como corretear por la casa y algún que otro salto inesperado, son parte del día a día. Es un pequeñajo, además de guapetón, simpático, divertido y muy amoroso. Sabe hacer sus cositas en el arenero y se ha adaptado muy bien a vivir entre personas.', '2021-06-07', 'Macho','https://elrefugioescuela.com/sofia/wp-content/uploads/2022/12/billy-portada-2-1024x576.jpg' ,2, 'BILLY debía de llevar mucho tiempo solito en la calle, sin refugio y protección ya que, en el momento del rescate, se encontraba muy sucio, asustado y con un hambre brutal. Por fortuna, ahora forma parte de nuestro Refugio y vive en la gatera con otros compis gatunos.', '-' );
INSERT INTO animal (id, nombre, apodo, aspectos_veterinarios, fecha_nacimiento, genero, foto, tipo_animal_id, historia, raza) VALUES (6, 'Blanca', 'BLANCA, de mirada fiel', ' BLANCA está sana. Los resultados del test de enfermedades dieron negativo en todas (erliquia, filaria, leishmaniosis). Tiene chip, cartilla veterinaria, está al día de vacunas, desparasitiación y está esterilizada. Preparada para encontrar por fin a una familia bonita que desee adoptarla. ', '2018-05-16', 'Hembra','https://elrefugioescuela.com/sofia/wp-content/uploads/2019/11/blanca-22.jpg' , 1, 'BLANCA sufrió el abandono dentro de su propio hogar. Vivía, junto con otros perros, en la azotea de una casa. La falta de socialización durante este tiempo, ha hecho que BLANCA necesite pautas para relacionarse con perros desconocidos. ', '' );
INSERT INTO animal (id, nombre, apodo, aspectos_veterinarios, fecha_nacimiento, genero, foto, tipo_animal_id, historia, raza) VALUES (7, 'Celeste', 'La gata CELESTE', 'CELESTE, además de ser un bellezón, es una gatita muy cariñosa y entregada a las personas. En su casa convivía feliz entre quienes cuidaban de ella y, en el refugio, se muestra mimosa con todos los voluntarios. Con el resto de gatos, poco a poco, va aceptando el cambio y cada día se relaciona más con sus compis gatunos. Es una gatita joven que con tiempo, paciencia y mucho cariño podría adaptarse a cualquier tipo de familia que le de una oportunidad.CELESTE dio positivo en inmunodeficiencia en el test de enfermedades. Es una enfermedad que tiene un riesgo de contagio bajo en el caso de nuestros gatos en adopción. Esto se debe a que tan solo se transmite a través de la sangre o por relaciones sexuales. Todos están esterilizados y en un ambiente de calma con todas sus necesides cubiertas, por lo que ambas opciones de transmisión no tienen presencia. Si tienes dudas al respecto, puedes consultar en cualquier veterinario. ', '2019-05-05', 'Hembra','https://elrefugioescuela.com/sofia/wp-content/uploads/2022/12/celeste-portada-1024x576.jpg' , 2, 'CELESTE vivía en una colonia de Sevilla hasta el pasado mes de junio de 2021 aproximadamente, momento en el que una buena amiga, la rescató, a ella y a sus bebés; acogidos, afortunadamente, en otra asociación, ya que nuestro refugio no está acondicionado para cachorritos. CELESTE pasó unos días con su familia de acogida para realizarle el test de enfermedades, vacunarla, chipearla y pasar el post operatorio tranquila y sin riesgo para su salud.', 'Maine Coon' );
INSERT INTO animal (id, nombre, apodo, aspectos_veterinarios, fecha_nacimiento, genero, foto, tipo_animal_id, historia, raza) VALUES (8, 'Aitor', 'EL GATO AITOR', 'AITOR es un gatito súper entrañable. Llegó hasta nosotros algo tímido e inseguro, pero a medida que fueron pasando los días, dejó escapar su verdadero yo gatuno ¡No sabes cuánto le gusta disfrutar de los mimos! Se ha vuelto el más cariñoso de toda la gatera. En cuanto uno de los voluntarios asoma la cabeza por la casita de nuestros felinos, AITOR ya comienza su ritual de estiramientos, maulliditos y puesta en práctica del rozamiento y conseguir así, engatusarte un poquito más cada día. Y nosotros … ¡tan felices! Es un gatito sociable, afectuoso, muy, muy cariñoso, respetuoso con sus compis felinos. Más que apto para comenzar una nueva vida junto a ti y rodeado de esa tranquilidad y seguridad que solo da el hogar.', '2020-11-20', 'Macho','https://elrefugioescuela.com/sofia/wp-content/uploads/2022/11/aitor-2-1024x1024.jpg' , 2, 'AITOR es un gato que ha vivido siempre en familia, rodeado de otros felinos y de personas. Pertenecía a un grupo numeroso, unos 15 gatos habitaban su anterior hogar, todos bien alimentados y cuidados. Desde principios del 2021 su rescatadora ha querido darle la oportunidad, junto a sus otros tres hermanos en adopción, FATIMA, CHUCHI y PATXI, de encontrar una familia donde vivir con mayor tranquilidad y recibir todos los mimitos y cariños. Los hermanos se pueden adoptar todos juntos, en pareja o por separado.', 'Siamés ' );
INSERT INTO animal (id, nombre, apodo, aspectos_veterinarios, fecha_nacimiento, genero, foto, tipo_animal_id, historia, raza) VALUES (9, 'Ludovico', 'El noble LUDOVICO', 'LUDOVICO está completamente sano. Se entrega con chip, pasaporte, vacunas al día, test de enfermedades negativo de anaplasma, erliquia, filaria y leishmaniosis. Además, se entrega esterilizado.', '2021-05-08', 'Macho','https://elrefugioescuela.com/sofia/wp-content/uploads/2023/06/1687710156192-1-926x1024.jpg' , 1, 'Se encontraba deambulando por la autovía hasta que una persona pudo parar y recogerlo. A pesar de las dificultades, ya que esta persona se dirijía a trabajar, no dudó en recogerlo y ayudarlo. Más personas así hacen falta en este mundo. No conocemos más de su pasado, pero sí conocemos en qué condiciones fue encontrado. Sucio, delgado y lleno de parásitos…no hace falta decir más.', 'Cruce' );
INSERT INTO animal (id, nombre, apodo, aspectos_veterinarios, fecha_nacimiento, genero, foto, tipo_animal_id, historia, raza) VALUES (10, 'Lena', 'LENA, una historia de superación', 'LENA se entrega con chip, pasaporte, vacunas al día y test de enfermedades de anaplasma, erliquia, filaria y leishmaniosis. Además, se entrega esterilizada.', '2022-04-05', 'Hembra','https://elrefugioescuela.com/sofia/wp-content/uploads/2024/04/IMG-20240404-WA0020-1024x576.jpg' , 1, 'Como suele pasar con los perros de caza, LENA ha debido de tener un pasado aterrador, solo hay que ver su pánico y esa mirada apagada, pero sabemos que detrás de esos ojos tristes hay una perra que desea con todas sus fuerzas confiar en las personas y conocer, por fin, la felicidad, esa que tanto se merece.', 'Mestiza' );
INSERT INTO animal (id, nombre, apodo, aspectos_veterinarios, fecha_nacimiento, genero, foto, tipo_animal_id, historia, raza) VALUES (11, 'Megan', 'La preciosa MEGAN', 'MEGAN es negativa en leucemia, inmunodeficiencia felina y calicivirus. Se entrega esterilizada, desparasitada, con chip y vacunas al día.', '2024-01-05', 'Hembra','https://elrefugioescuela.com/sofia/wp-content/uploads/2024/04/IMG-20240423-WA0038-1024x576.jpg' , 2, 'Rescatada junto a su hermanito HARRY cuando estaban recién nacidos, han crecido sanos y fuertes como campeones que son y ahora les toca encontrar una familia definitiva. MEGAN es una pequeña aventurera, un poco más pequeña que su hermano HARRY pero dispuesta a comerse el mundo. Es tierna y cariñosa.', 'American shorthair' );
INSERT INTO animal (id, nombre, apodo, aspectos_veterinarios, fecha_nacimiento, genero, foto, tipo_animal_id, historia, raza) VALUES (12, 'Harry', 'Súper HARRY', 'HARRY es negativo en leucemia, inmunodeficiencia felina y calicivirus. Se entrega esterilizado, desparasitado, con chip y vacunas al día.', '2024-01-05', 'Macho','https://elrefugioescuela.com/sofia/wp-content/uploads/2024/04/IMG-20240423-WA0039-1024x576.jpg' , 2, 'Rescatado junto a su hermanita MEGAN cuando estaban recién nacidos, han crecido sanos y fuertes como campeones que son y ahora les toca encontrar una familia definitiva. HARRY es todo un valiente. A pesar de que ha sido complicado sacarlos adelante, ambos lo han logrado. Él es cariñoso y curioso, está deseando descubrir y conocer el mundo.', 'American shorthair' );
